<style lang="scss" scoped>
.profile {
    &__nav {
        border-radius: 20px;
        background: var(--white, #FFF);
        width: fit-content;
        padding: 0px 20px;
        height: fit-content;
        transition: .2s;
        @media (max-width: 1023px) {
            flex: 0 0 100%;
            padding: 0px;
        }
        @media (max-width: 539px) {
        }
    }

    &__nav-block {
        &:not(:last-child) {
            margin-bottom: 20.5px;
            border-bottom: 1px solid var(--var-line);
            padding-bottom: 20.5px;
            @media (max-width: 539px) {
                margin-bottom: 15px;
                padding-bottom: 15px;
            }
        }
        & a {
            display: flex;
            align-items: center;
            gap: 8px;
            border-radius: var(--rounded-lg, 8px);
            padding: 10px 20px;
            @media (max-width: 539px) {
                padding: 5px 10px;
            }
            &.router-link-active {
                background: var(--blue-50, #EBF5FF);
                & svg {
                    & path {
                        stroke: #003791;
                    }
                }
                & span {
                    color: var(--primary-2-default, #003791);
                }
            }
            &:not(:last-child) {
                margin-bottom: 15px;
            }

            & svg {

            }

            & span {
                color: var(--gray-800, #1F2A37);
                font-family: Roboto;
                font-size: 14px;
                font-style: normal;
                font-weight: 500;
                line-height: 150%; /* 21px */
                @media (max-width: 539px) {
                    font-size: 13px;
                }
            }
        }

    }
}

[dark=true] {
    & .profile__nav {
        background: var(--var-body);
    }

    & .profile__nav-block {
        & a {
            &.router-link-active {
                background: var(--gray-700, #374151);
                & svg {
                    & path {
                        stroke: #3F83F8;
                    }
                }
                & span {
                    color: #3F83F8;
                }
            }

            & svg {
                & path {
                    stroke: #E5E7EB;
                }
            }

            & span {
                color: var(--gray-200, #E5E7EB);
            }
        }
    }
}

</style>